pub enum ArchivedRcWeak<T: ArchivePointee + ?Sized, F> {
    None,
    Some(ArchivedRc<T, F>),
}

pub enum AllocScratchError {
    ExceededLimit {
        requested: usize,
        remaining: usize,
    },
    NotPoppedInReverseOrder {
        expected: *mut u8,
        expected_layout: Layout,
        actual: *mut u8,
        actual_layout: Layout,
    },
    NoAllocationsToPop,
}

pub enum BufferSerializerError {
    Overflow {
        pos: usize,
        bytes_needed: usize,
        archive_len: usize,
    },
}

pub enum FixedSizeScratchError {
    OutOfScratch(Layout),
    NotPoppedInReverseOrder {
        pos: usize,
        next_pos: usize,
        next_size: usize,
    },
    UnownedAllocation,
}

pub enum CompositeSerializerError<S, C, H> {
    SerializerError(S),
    ScratchSpaceError(C),
    SharedError(H),
}
